mapper.select:

select 

from M_ORDER_TXN_DETAIL
where MOTD_TXN_ID = #{motdTxnId,jdbcType=VARCHAR}
  and MOTD_CARD_NO = #{motdCardNo,jdbcType=VARCHAR}

mapper.delete:

delete from M_ORDER_TXN_DETAIL
where MOTD_TXN_ID = #{motdTxnId,jdbcType=VARCHAR}
  and MOTD_CARD_NO = #{motdCardNo,jdbcType=VARCHAR}
